import React, { useEffect, useState } from 'react';
import { Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
function ListEmployee() {
  const [employees, setEmployees] = useState([]);
  const navigate = useNavigate();
  
  useEffect(() => {
    fetch('http://localhost:8080/employees')
      .then(res => {
        if (!res.ok) throw new Error('Failed to fetch employees');
        return res.json();
      })
      .then(data => setEmployees(data))
      .catch(err => console.error('Error:', err));
  }, []);
const handleUpdate = (employee) => {
  navigate(`/edit_employee/${employee.id}`);
  }

  const handleDelete = async (id) => {
  if (window.confirm('Are you sure you want to delete this employee?')) {
    try {
      const res = await fetch(`http://localhost:8080/employees/${id}`, {
        method: 'DELETE'
      });
      if (res.ok) {
        setEmployees(prev => prev.filter(emp => emp.id !== id));
      } else {
        alert('Failed to delete employee');
      }
    } catch (error) {
      console.error('Delete error:', error);
    }
  }
};

  return (
    <div>
      <h1 className="text-center mt-5">List of Employees</h1>
      <div className="container mt-5">
        <table className="table table-striped table-dark">
          <thead>
            <tr>
              <th>Id</th>
              <th>Name</th>
              <th>Email</th>
              <th>Department</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {employees.length > 0 ? (
              employees.map((employee) => (
                <tr key={employee.id}>
                  <td>{employee.id}</td>
                  <td>{employee.name}</td>
                  <td>{employee.email}</td>
                  <td>{employee.department}</td>
                  <td>
                    <div className='d-flex justify-content-start align-items-center gap-2'>
                    <Button variant="warning" onClick={() => handleUpdate(employee)}>Update</Button>
                    <Button variant="danger" onClick={() => handleDelete(employee.id)}>Delete</Button>
                    </div>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center">No employees found.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ListEmployee;
