import React, { useState } from 'react';
import { Form, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

function Add() {
    const navigate = useNavigate();
    const [postEmployee, setPostEmployee] = useState({
        name: '',
        email: '',
        department: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPostEmployee(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(postEmployee);
        navigate('/employee'); 
    };

    const handleClose = () => {
        navigate('/');
    };

    return (
        <div className="container mt-5 d-flex flex-column align-items-center justify-content-center">
            <Form onSubmit={handleSubmit}>
                <h2 className="text-center mb-4">Add Employee</h2>
                <Form.Group controlId="formName" className='mb-3'>
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                        type="text"
                        name="name"
                        placeholder="Enter name"
                        value={postEmployee.name}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="formEmail" className='mb-3'>
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                        type="email"
                        name="email"
                        placeholder="Enter email"
                        value={postEmployee.email}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Form.Group controlId="formDepartment">
                    <Form.Label>Department</Form.Label>
                    <Form.Control
                        type="text"
                        name="department"
                        placeholder="Enter department"
                        value={postEmployee.department}
                        onChange={handleChange}
                    />
                </Form.Group>

                <Button variant="primary" type="submit" className="mt-3">
                    Submit
                </Button>
                <Button variant="secondary" className="ms-2 mt-3" onClick={handleClose}>
                    Cancel
                </Button>
            </Form>
        </div>
    );
}

export default Add;
